
public class AddTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float a=10.0f, b=20.0f, sum;
		sum=a+b;
		System.out.println("sum of two float numbers is "+ sum);
		int x=12, y=13, sumint;
		sumint=x+y;
		System.out.println("sum of two int numbers is "+ sumint);
		double x1=12, y1=13, sumdob;
		sumdob=x1+y1;
		System.out.println("sum of two double numbers is "+ sumdob);
		long x2=13, y2=13, sumlon;
		sumlon = x2+ y2;
		System.out.println("sum of two long numbers is "+ sumlon);
	}

}
