//Write a program to find if a character entered is vowel or not.
public class CharacterVowelOrConsonent {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch ='O';
		if (ch=='a'|| ch=='A'|| ch=='e'|| ch=='E'|| ch=='i'|| ch=='I'|| ch=='o'|| ch=='O' || ch=='u'|| ch=='U')
			System.out.println("Character " +ch+ " entered is a vowel");
		else
			System.out.println("Character " +ch+ " entered is a consonant");


	}

}
