
public class SmallestOfThreeNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10, b=2, c=1;
if ((a<b) && (a<c))
	System.out.println(a + " is smallest");
else if (b<c)
	System.out.println(b+ " is smallest");
else 
	System.out.println(c+ " is smallest");

		
		
	}

}
